MyCmsBean extends CmsJspBean {


    /**
     * It is important to call this method after bean instantiation.
     *
     * @param context the JSP page context object
     * @param req the JSP request
     * @param res the JSP response
     */
    @Override
    public void init(PageContext context, HttpServletRequest req, HttpServletResponse res) {
        super.init(context, req, res);

        /* Do something meaningfull here .. */

    }

     /**
     * Example call to method from superclass. 
     * Only works if init is called with neccessary params.
     */
    public String getUri() {
        return getCmsObject().getRequestContext().getUri();
    }

}
